import React from "react";
import {storiesOf} from "@storybook/react";

import {Button} from "../components/Button";

const stories = storiesOf('App test', module);

stories.add('Primary',() => {
    return (<Button text="Primary" variant="primary"/>);
})

stories.add('Secondary',()=>{
    return (<Button text="Secondary" variant="secondary" />);
})
stories.add('Ghost',()=>{
    return (<Button text="Ghost" variant="ghost" />);
})
stories.add('Underline',()=>{
    return (<Button text="Underline" variant="underline" />);
})