import React from "react";
import {storiesOf} from "@storybook/react";

import {List} from "../components/List";
import {ListItem} from "../components/List"
const stories=storiesOf('List', module);

stories.add('Unordered',()=>{
    return (<List type="unordered">
    <ListItem>This is first List Item</ListItem>
    <ListItem>This is second List Item</ListItem>
    <ListItem>This is third List Item</ListItem>
 </List>)
})

stories.add('Ordered',()=>{
    return (<List type="ordered">
    <ListItem>This is first List Item</ListItem>
    <ListItem>This is second List Item</ListItem>
    <ListItem>This is third List Item</ListItem>
</List>)
})

stories.add('no-bullet',()=>{
    return(
        <List type="no-bullet">
    <ListItem>This is first List Item</ListItem>
    <ListItem>This is second List Item</ListItem>
    <ListItem>This is third List Item</ListItem>
</List>
    )
})